window.onload=function(){slideOne(),slideTwo()};let sliderOne=document.getElementById("slider-1"),sliderTwo=document.getElementById("slider-2"),displayValOne=document.getElementById("range1"),displayValTwo=document.getElementById("range2"),minGap=0,sliderTrack=document.querySelector(".slider-track"),sliderMaxValue=document.getElementById("slider-1").max;function slideOne(){parseInt(sliderTwo.value)-parseInt(sliderOne.value)<=minGap&&(sliderOne.value=parseInt(sliderTwo.value)-minGap),displayValOne.textContent="от "+sliderOne.value,fillColor()}function slideTwo(){parseInt(sliderTwo.value)-parseInt(sliderOne.value)<=minGap&&(sliderTwo.value=parseInt(sliderOne.value)+minGap),displayValTwo.textContent="до "+sliderTwo.value,fillColor()}function fillColor(){percent1=sliderOne.value/sliderMaxValue*100,percent2=sliderTwo.value/sliderMaxValue*100,sliderTrack.style.background = `linear-gradient(to right, #dbdbdb ${percent1}% , #91c92f ${percent1}% , #91c92f ${percent2}%, #dbdbdb ${percent2}%)`}