var menuBtn=document.querySelector(".catalog-opener"),menu=document.querySelector(".header-catalog");menuBtn.addEventListener("click",function(){menu.classList.toggle("header-catalog-show")}),document.addEventListener("mouseup",function(e){menu.classList.contains("header-catalog-show")&&(menu===e.target||menu.contains(e.target)||menuBtn===e.target||menu.classList.remove("header-catalog-show"))});